import React, { useContext, useEffect, useState } from 'react';
import '../../styles/client/ProjectWorking.css';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { GeneralContext } from '../../context/GeneralContext';
import { FaComments } from 'react-icons/fa';

const ProjectWorking = () => {
  const { socket } = useContext(GeneralContext);
  const { id: projectId } = useParams();
  const clientId = localStorage.getItem('userId');

  const [project, setProject] = useState(null);
  const [chats, setChats] = useState([]);
  const [message, setMessage] = useState('');
  const [isChatOpen, setIsChatOpen] = useState(false);

  useEffect(() => {
    socket.emit('join-chat-room', { projectId, freelancerId: '' });
    fetchProject();
    fetchChats();

    socket.on('message-from-user', fetchChats);
    return () => socket.off('message-from-user', fetchChats);
  }, [projectId]);

  const fetchProject = async () => {
    const res = await axios.get(`http://localhost:6001/fetch-project/${projectId}`);
    setProject(res.data);
  };

  const fetchChats = async () => {
    const res = await axios.get(`http://localhost:6001/fetch-chats/${projectId}`);
    setChats(res.data.messages || []);
  };

  const handleMessageSend = () => {
    if (!message.trim()) return;
    socket.emit('new-message', {
      projectId,
      senderId: clientId,
      message,
      time: new Date().toISOString(),
    });
    setMessage('');
    fetchChats();
  };

  return (
    <>
      {project && (
        <div className="project-data-page">
          <div className="project-data-container">
            <div className="project-data">
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              <span>
                <h5>Required skills</h5>
                <div className="required-skills">
                  {project.skills.map((skill) => (
                    <p key={skill}>{skill}</p>
                  ))}
                </div>
              </span>
              <span>
                <h5>Budget</h5>
                <h6>₹ {project.budget}</h6>
              </span>
            </div>

            {project.submission ? (
              <div className="project-submissions-container">
                <h4>Submission</h4>
                <div className="project-submission">
                  <p>No submissions yet!!</p>
                </div>
              </div>
            ) : null}
          </div>

          {/* Floating Chat Button */}
          <div className="chat-toggle-icon" onClick={() => setIsChatOpen(!isChatOpen)}>
            <FaComments size={24} />
          </div>

          {/* Chat Popup */}
          {isChatOpen && (
            <div className="chat-popup">
              <div className="chat-header">
                <h5>Chat with the Freelancer</h5>
                <button className="btn-close" onClick={() => setIsChatOpen(false)} />
              </div>

              <div className="chat-body">
                <div className="chat-messages">
                  {chats.map((msg, idx) => (
                    <div
                      key={idx}
                      className={msg.senderId === clientId ? 'my-message' : 'received-message'}
                    >
                      <div>
                        <p>{msg.text}</p>
                        <h6>{msg.time?.slice(5, 10)} - {msg.time?.slice(11, 19)}</h6>
                      </div>
                    </div>
                  ))}
                </div>

                <div className="chat-input">
                  <input
                    type="text"
                    placeholder="Enter message..."
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                  />
                  <button onClick={handleMessageSend}>Send</button>
                </div>
              </div>
            </div>
          )}
        </div>
      )}
    </>
  );
};

export default ProjectWorking;
