import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import '../../styles/freelancer/ProjectData.css';
import { GeneralContext } from '../../context/GeneralContext';

const ProjectData = () => {
  const { socket } = useContext(GeneralContext);
  const { id: projectId } = useParams();

  const [project, setProject] = useState(null);
  const [clientId, setClientId] = useState('');
  const freelancerId = localStorage.getItem('userId');

  const [proposal, setProposal] = useState('');
  const [bidAmount, setBidAmount] = useState('');
  const [estimatedTime, setEstimatedTime] = useState('');

  const [projectLink, setProjectLink] = useState('');
  const [manualLink, setManualLink] = useState('');
  const [submissionDescription, setSubmissionDescription] = useState('');

  const [message, setMessage] = useState('');
  const [chats, setChats] = useState([]);

  const [showChatBox, setShowChatBox] = useState(false); // ðŸ‘ˆ chat toggle

  useEffect(() => {
    fetchProject();
    joinSocketRoom();
    fetchChats();
  }, [projectId]);

  const joinSocketRoom = () => {
    socket.emit('join-chat-room', { projectId, freelancerId });
  };

  const fetchProject = async () => {
    try {
      const res = await axios.get(`http://localhost:6001/fetch-project/${projectId}`);
      setProject(res.data);
      setClientId(res.data.clientId);
    } catch (err) {
      console.error('Error fetching project:', err);
    }
  };

  const handleBidding = async () => {
    if (!clientId || !freelancerId || !projectId || !proposal || !bidAmount || !estimatedTime) {
      alert('âš ï¸ Please fill all fields before submitting your bid.');
      return;
    }

    const bidData = {
      clientId,
      freelancerId,
      projectId,
      proposal,
      bidAmount: parseInt(bidAmount),
      estimatedTime: parseInt(estimatedTime),
    };

    try {
      const response = await axios.post('http://localhost:6001/make-bid', bidData);
      alert('âœ… Bidding successful!');
      setProposal('');
      setBidAmount('');
      setEstimatedTime('');
      fetchProject(); // Refresh after successful bid
    } catch (err) {
      alert("âŒ Bidding failed: " + (err.response?.data?.error || "Unknown error"));
    }
  };

  const handleProjectSubmission = async () => {
    try {
      await axios.post('http://localhost:6001/submit-project', {
        clientId,
        freelancerId,
        projectId,
        projectLink,
        manualLink,
        submissionDescription,
      });
      setProjectLink('');
      setManualLink('');
      setSubmissionDescription('');
      alert('âœ… Submission successful!');
      fetchProject();
    } catch (err) {
      alert('âŒ Submission failed!');
    }
  };

  const handleMessageSend = () => {
    if (!message.trim()) return;
    socket.emit('new-message', {
      projectId,
      senderId: freelancerId,
      message,
      time: new Date(),
    });
    setMessage('');
    fetchChats();
  };

  const fetchChats = async () => {
    try {
      const res = await axios.get(`http://localhost:6001/fetch-chats/${projectId}`);
      setChats(res.data.messages || []);
    } catch (err) {
      console.error('Error fetching chats:', err);
    }
  };

  useEffect(() => {
    socket.on('message-from-user', fetchChats);
    return () => socket.off('message-from-user', fetchChats);
  }, [socket]);

  if (!project) return <p>Loading project...</p>;

  const alreadyBidded = project.bids.includes(freelancerId);

  return (
    <div className="project-data-page">
      <div className="project-data-container">
        <div className="project-data">
          <h3>{project.title}</h3>
          <p>{project.description}</p>

          <span>
            <h5>Required skills</h5>
            <div className="required-skills">
              {project.skills.map((skill) => (
                <p key={skill}>{skill}</p>
              ))}
            </div>
          </span>

          <span>
            <h5>Budget</h5>
            <h6>â‚¹ {project.budget}</h6>
          </span>
        </div>

        {project.status === 'Available' && (
          <div className="project-form-body">
            <h4>Send Proposal</h4>

            <div className="form-floating">
              <input
                type="number"
                className="form-control mb-3"
                placeholder="Bid amount"
                value={bidAmount}
                onChange={(e) => setBidAmount(e.target.value)}
              />
              <label>Budget (â‚¹)</label>
            </div>

            <div className="form-floating">
              <input
                type="number"
                className="form-control mb-3"
                placeholder="Estimated time"
                value={estimatedTime}
                onChange={(e) => setEstimatedTime(e.target.value)}
              />
              <label>Estimated time (days)</label>
            </div>

            <div className="form-floating">
              <textarea
                className="form-control mb-3"
                placeholder="Proposal description"
                value={proposal}
                onChange={(e) => setProposal(e.target.value)}
              />
              <label>Describe your proposal</label>
            </div>

            {alreadyBidded ? (
              <button className="btn btn-primary" disabled>
                Already bidded
              </button>
            ) : (
              <button className="btn btn-success" onClick={handleBidding}>
                Post Bid
              </button>
            )}
          </div>
        )}

        {project.freelancerId === freelancerId && (
          <div className="project-form-body">
            <h4>Submit the Project</h4>

            {project.submissionAccepted ? (
              <p>âœ… Project marked as completed</p>
            ) : (
              <>
                <div className="form-floating">
                  <input
                    type="text"
                    className="form-control mb-3"
                    placeholder="Project link"
                    value={projectLink}
                    onChange={(e) => setProjectLink(e.target.value)}
                  />
                  <label>Project Link</label>
                </div>

                <div className="form-floating">
                  <input
                    type="text"
                    className="form-control mb-3"
                    placeholder="Manual link"
                    value={manualLink}
                    onChange={(e) => setManualLink(e.target.value)}
                  />
                  <label>Manual Link</label>
                </div>

                <div className="form-floating">
                  <textarea
                    className="form-control mb-3"
                    placeholder="Submission description"
                    value={submissionDescription}
                    onChange={(e) => setSubmissionDescription(e.target.value)}
                  />
                  <label>Describe your work</label>
                </div>

                {project.submission ? (
                  <button className="btn btn-secondary" disabled>
                    Already submitted
                  </button>
                ) : (
                  <button className="btn btn-success" onClick={handleProjectSubmission}>
                    Submit Project
                  </button>
                )}
              </>
            )}
          </div>
        )}
      </div>

      {/* Chat Toggle Button */}
      {project.freelancerId === freelancerId && (
        <>
          <button
            onClick={() => setShowChatBox(!showChatBox)}
            style={{
              position: 'fixed',
              bottom: '20px',
              right: '20px',
              zIndex: 999,
              background: '#007bff',
              color: 'white',
              borderRadius: '50%',
              padding: '12px 16px',
              fontSize: '20px',
              border: 'none',
              boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
            }}
            title="Open chat"
          >
            ðŸ’¬
          </button>

          {showChatBox && (
            <div
              className="project-chat-container"
              style={{
                position: 'fixed',
                bottom: '80px',
                right: '20px',
                width: '350px',
                maxHeight: '400px',
                backgroundColor: 'white',
                border: '1px solid #ccc',
                borderRadius: '12px',
                boxShadow: '0 4px 16px rgba(0,0,0,0.3)',
                zIndex: 998,
                display: 'flex',
                flexDirection: 'column'
              }}
            >
              <div style={{ padding: '10px', backgroundColor: '#007bff', color: 'white', borderTopLeftRadius: '12px', borderTopRightRadius: '12px' }}>
                <strong>Chat with Client</strong>
                <button
                  onClick={() => setShowChatBox(false)}
                  style={{ float: 'right', background: 'transparent', border: 'none', color: 'white', fontSize: '16px' }}
                >
                  âœ–
                </button>
              </div>

              <div className="chat-body" style={{ padding: '10px', overflowY: 'auto', flex: 1 }}>
                {chats.map((msg, idx) => (
                  <div
                    key={idx}
                    className={msg.senderId === freelancerId ? 'my-message' : 'received-message'}
                    style={{
                      textAlign: msg.senderId === freelancerId ? 'right' : 'left',
                      margin: '6px 0',
                    }}
                  >
                    <div
                      style={{
                        display: 'inline-block',
                        backgroundColor: msg.senderId === freelancerId ? '#dcf8c6' : '#f1f0f0',
                        padding: '8px 12px',
                        borderRadius: '15px',
                      }}
                    >
                      <p style={{ margin: 0 }}>{msg.text}</p>
                      <small style={{ fontSize: '10px', color: '#888' }}>
                        {msg.time?.slice(5, 10)} {msg.time?.slice(11, 19)}
                      </small>
                    </div>
                  </div>
                ))}
              </div>

              <div className="chat-input" style={{ padding: '10px', borderTop: '1px solid #ccc' }}>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Enter message..."
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                />
                <button className="btn btn-sm btn-primary mt-2" onClick={handleMessageSend}>
                  Send
                </button>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default ProjectData;
