import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { GeneralContext } from '../../context/GeneralContext';

const AllProjects = () => {
  const { socket } = useContext(GeneralContext);

  const [projects, setProjects] = useState([]);
  const [displayProjects, setDisplayProjects] = useState([]);
  const [search, setSearch] = useState('');
  const [allSkills, setAllSkills] = useState([]);
  const [filterSkills, setFilterSkills] = useState([]);

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get('http://localhost:6001/fetch-projects');
      const data = response.data;

      setProjects(data);
      setDisplayProjects([...data].reverse());

      // Build unique skills list without mutating state directly
      const skillsSet = new Set();
      data.forEach((project) => {
        project.skills.forEach((skill) => {
          skillsSet.add(skill);
        });
      });

      setAllSkills([...skillsSet]);
    } catch (err) {
      console.error("Failed to fetch projects:", err);
    }
  };

  const handleSkillFilter = (skill) => {
    let newFilters = [...filterSkills];
    if (newFilters.includes(skill)) {
      newFilters = newFilters.filter((s) => s !== skill);
    } else {
      newFilters.push(skill);
    }
    setFilterSkills(newFilters);
    applyFilters(newFilters, search);
  };

  const applyFilters = (skills, keyword) => {
    let filtered = [...projects];
    if (skills.length > 0) {
      filtered = filtered.filter((project) =>
        skills.every((skill) => project.skills.includes(skill))
      );
    }
    if (keyword.trim() !== '') {
      filtered = filtered.filter((project) =>
        project.title.toLowerCase().includes(keyword.toLowerCase())
      );
    }
    setDisplayProjects(filtered.reverse());
  };

  const handleSearchChange = (e) => {
    const keyword = e.target.value;
    setSearch(keyword);
    applyFilters(filterSkills, keyword);
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">All Projects</h2>

      <input
        type="text"
        className="form-control mb-3"
        placeholder="Search by title..."
        value={search}
        onChange={handleSearchChange}
      />

      <div className="mb-3">
        <strong>Filter by Skills:</strong>
        <div className="d-flex flex-wrap mt-2">
          {allSkills.map((skill, index) => (
            <button
              key={index}
              className={`btn me-2 mb-2 ${
                filterSkills.includes(skill) ? 'btn-primary' : 'btn-outline-secondary'
              }`}
              onClick={() => handleSkillFilter(skill)}
            >
              {skill}
            </button>
          ))}
        </div>
      </div>

      <div className="row">
        {displayProjects.length === 0 ? (
          <p>No projects found.</p>
        ) : (
          displayProjects.map((project) => (
            <div className="col-md-6 mb-4" key={project._id}>
              <div className="card p-3 shadow-sm h-100">
                <h5>{project.title}</h5>
                <p>{project.description}</p>
                <p>
                  <strong>Budget:</strong> ${project.budget}
                </p>
                <p>
                  <strong>Skills:</strong> {project.skills.join(', ')}
                </p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default AllProjects;
