import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import '../../styles/client/ProjectWorking.css';
import { GeneralContext } from '../../context/GeneralContext';
import { FaComments, FaTimes } from 'react-icons/fa';

const ProjectWorking = () => {
  const { socket } = useContext(GeneralContext);
  const { id: projectId } = useParams();

  const [project, setProject] = useState(null);
  const [chats, setChats] = useState([]);
  const [message, setMessage] = useState('');
  const [isChatOpen, setIsChatOpen] = useState(false);

  const clientId = localStorage.getItem('userId');

  // Join socket room
  useEffect(() => {
    if (socket && projectId) {
      socket.emit('join-chat-room', { projectId, freelancerId: '' });
    }
  }, [socket, projectId]);

  // Fetch project data
  const fetchProject = async () => {
    try {
      const res = await axios.get(`http://localhost:6001/fetch-project/${projectId}`);
      setProject(res.data);
    } catch (err) {
      console.error('Error fetching project:', err);
    }
  };

  useEffect(() => {
    fetchProject();
  }, [projectId]);

  // Fetch chats
  const fetchChats = async () => {
    try {
      const res = await axios.get(`http://localhost:6001/fetch-chats/${projectId}`);
      setChats(res.data.messages || []);
    } catch (err) {
      console.error('Error fetching chats:', err);
    }
  };

  useEffect(() => {
    fetchChats();
  }, [projectId]);

  useEffect(() => {
    if (!socket) return;
    socket.on('message-from-user', fetchChats);
    return () => socket.off('message-from-user', fetchChats);
  }, [socket]);

  const handleMessageSend = () => {
    if (!message.trim()) return;
    socket.emit('new-message', {
      projectId,
      senderId: clientId,
      message,
      time: new Date().toISOString(),
    });
    setMessage('');
    fetchChats();
  };

  const handleApproveSubmission = async () => {
    try {
      await axios.get(`http://localhost:6001/approve-submission/${projectId}`);
      fetchProject();
      alert('✅ Submission approved!');
    } catch (err) {
      console.error(err);
      alert('❌ Operation failed!');
    }
  };

  const handleRejectSubmission = async () => {
    try {
      await axios.get(`http://localhost:6001/reject-submission/${projectId}`);
      fetchProject();
      alert('❌ Submission rejected!');
    } catch (err) {
      console.error(err);
      alert('❌ Operation failed!');
    }
  };

  if (!project) return <p>Loading project...</p>;

  return (
    <div className="project-data-page">
      {/* LEFT: PROJECT DETAILS + SUBMISSION */}
      <div className="project-data-container">
        <div className="project-data">
          <h3>{project.title}</h3>
          <p>{project.description}</p>

          <div className="required-section">
            <h5>Required skills</h5>
            <div className="required-skills">
              {project.skills.map((skill) => (
                <p key={skill}>{skill}</p>
              ))}
            </div>
          </div>

          <div className="budget-section">
            <h5>Budget</h5>
            <h6>₹ {project.budget}</h6>
          </div>
        </div>

        {project.freelancerId && (
          <div className="project-submissions-container">
            <h4>Submission</h4>
            <div className="project-submissions">
              {project.submission ? (
                <div className="project-submission">
                  <span>
                    <h5>Project Link:</h5>
                    <a href={project.projectLink} target="_blank" rel="noopener noreferrer">
                      {project.projectLink}
                    </a>
                  </span>

                  <span>
                    <h5>Manual Link:</h5>
                    <a href={project.manulaLink} target="_blank" rel="noopener noreferrer">
                      {project.manulaLink}
                    </a>
                  </span>

                  <h5>Description for work</h5>
                  <p>{project.submissionDescription}</p>

                  {project.submissionAccepted ? (
                    <h5 style={{ color: 'green' }}>Project completed ✅</h5>
                  ) : (
                    <div className="submission-btns">
                      <button className="btn btn-success" onClick={handleApproveSubmission}>
                        Approve
                      </button>
                      <button className="btn btn-danger" onClick={handleRejectSubmission}>
                        Reject
                      </button>
                    </div>
                  )}
                </div>
              ) : (
                <p>No submissions yet!</p>
              )}
            </div>
          </div>
        )}
      </div>

      {/* CHAT ICON (Floating) */}
      <div className="chat-float-icon" onClick={() => setIsChatOpen(!isChatOpen)}>
        {isChatOpen ? <FaTimes size={22} /> : <FaComments size={22} />}
      </div>

      {/* CHAT PANEL */}
      {isChatOpen && (
        <div className="chat-popup-window">
          <h5>Chat with Freelancer</h5>
          <div className="chat-body">
            <div className="chat-messages">
              {chats.length > 0 ? (
                chats.map((msg, idx) => (
                  <div
                    key={idx}
                    className={msg.senderId === clientId ? 'my-message' : 'received-message'}
                  >
                    <div>
                      <p>{msg.text}</p>
                      <h6>
                        {msg.time?.slice(5, 10)} - {msg.time?.slice(11, 19)}
                      </h6>
                    </div>
                  </div>
                ))
              ) : (
                <p>No messages yet.</p>
              )}
            </div>

            <div className="chat-input">
              <input
                type="text"
                className="form-control"
                placeholder="Enter message..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
              />
              <button onClick={handleMessageSend}>Send</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProjectWorking;
